import UIKit
import PromiseKit

class PhotosInteractor: PhotosInteractorProtocol {

    weak var presenter: PhotosPresenterProtocol?
    
    func fetch(with id: Int) {
        firstly {
            PhotoService.fetch(with: id)
        }.done { list in
            self.presenter?.onSuccessRequest(list)
        }.catch { error in
            self.presenter?.onFailureRequest(error.localizedDescription)
        }
    }
    
}
